mapper.select:
select tes.* from tb_equipment_status tes,tb_equipment te

	tes.id = te.id


		and te.country = #{country}



		and tes.trans_status = #{transStatus}



		and tes.running_status = #{runningStatus}



		and tes.module_status = #{moduleStatus}



		and tes.banknote_box_status = #{banknoteBoxStatus}



		and tes.network_status = #{networkStatus}


		and tes.id like "%"#{id}"%"




ORDER BY te.create_time desc

select tes.* from tb_equipment_status tes,tb_equipment te where tes.trans_status != '0' and tes.trans_status != '4'
and tes.trans_status != '9' and te.id = tes.id
and tes.trans_status = #{transStatus}
and te.country = #{country}
and te.id = "%"#{id}"%"
order by tes.trans_status desc

select tes.* from tb_equipment_status tes,tb_equipment te where
tes.connect_status != '0' and tes.connect_status!=9 and tes.id = te.id

	and tes.connect_status = #{connectStatus}


	and te.country = #{country}


	and te.id = "%"#{id}"%"

order by tes.connect_status desc

select tes.* from tb_equipment_status tes ,tb_equipment te where tes.module_status != '0' and tes.module_status != '4' and tes.module_status != '9' and tes.id = te.id
and tes.module_status = #{moduleStatus}
and te.country = #{country}
and te.id = "%"#{id}"%"
order by tes.module_status desc

select tes.* from tb_equipment_status tes,tb_equipment te where tes.banknote_box_status != '0' and tes.banknote_box_status != '5' and tes.banknote_box_status != '9' and tes.id = te.id
and tes.banknote_box_status = #{banknoteBoxStatus}
and te.country = #{country}
and te.id = "%"#{id}"%"
order by tes.banknote_box_status desc

select tes.* from tb_equipment_status tes ,tb_equipment te where tes.network_status != '0' and tes.network_status != '4' and tes.network_status != '9' and tes.id = te.id
and tes.network_status = #{networkStatus}
and te.country = #{country}
and te.id = "%"#{id}"%"
order by tes.network_status desc

select tes.* from tb_equipment_status tes ,tb_equipment te where tes.prr_status != '0' and tes.prr_status != '4' and tes.prr_status != '9' and tes.id = te.id
and tes.prr_status = #{prrStatus}
and te.country = #{country}
and te.id = "%"#{id}"%"
order by tes.prr_status desc

select tes.* from tb_equipment_status tes ,tb_equipment te where
tes.maintain_status != '0' and tes.id = te.id

	and tes.maintain_status = #{maintainStatus}


	and te.country = #{country}


	and te.id = "%"#{id}"%"


order by tes.maintain_status desc
