mapper.select:
		select te.id id,a.area_name country,te.detail_address
		detailAddress,test.equipment_type equipmentType,
		test.equipment_sub_type equipmentSubType,test.online_time onlineTime
		,tes.running_status runningStatus,test.install_location installLocation,
		test.create_admin_name createAdminName ,(case when tes.running_status = '2' then te.down_time end ) downTime
        from
		tb_equipment te
        left join tb_equipment_settings test on te.id = test.id
        left join tb_equipment_status tes on te.id = tes.id
        left join tb_area a on te.country_id = a.id


                and te.country_id = #{countryId}



                and test.equipment_type = #{equipmentSubType}



                and tes.running_status = #{runningStatus}



                and test.id like '%${id}%'



                and test.equipment_sub_type = #{equipmentType}



                and tes.running_status = #{runningStatus}

select tes.id id , tes.equipment_type equipmentType ,tes.equipment_sub_type equipmentSubType,tes.equipment_version equipmentVersion,
tes.soft_version softVersion,tes.brand brand,tes.operateos operateos,
tes.browser browser, tes.install_location installLocation,
tes.pre_ip preIp,tes.pre_port prePort,tes.monitor_port monitorPort,
tes.monitor_file monitorFile,tes.version_server_port versionServerPort,
tes.version_server_file_port versionServerFilePort,tes.local_monitor_port localMonitorPort,
tes.local_file_monitor_port localFileMonitorPort,tes.limit_per_day limitPerDay,
tes.currency_code currencyCode,tes.master_key masterKey,tes.amount amount,
tes.private_key privateKey,tes.online_time onlineTime,tes.use_state useState, tes.create_admin_id createAdminId,tes.create_admin_name createAdminName,te.verify_status verifyStatus,te.create_time createTime,a.area_name country ,te.country_id countryId, test.running_status runningStatus from tb_equipment te left join tb_equipment_status test on test.id = te.id left join tb_equipment_settings tes on tes.id = te.id left join tb_area a on te.country_id = a.id
and te.id like "%"#{id}"%"
and tes.use_state = #{useState}
and te.verify_status = #{verifyStatus}
and tes.equipment_sub_type = #{equipmentSubType}
and te.country_id = #{countryId}
and tes.online_time >= #{firstTime}
and tes.online_time < #{endTime}
and test.running_status = #{runningStatus}
ORDER BY te.create_time desc
